import React, { useState } from "react";
import { NavLink } from "react-router-dom";

function Navbar({}) {
  const [activeTab, setActiveTab] = useState({tab});

  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
  };

  const linkClass = (tabName) =>
    `px-4 py-2 rounded-md ${
      activeTab === tabName ? "bg-blue-600 text-white" : "text-gray-600 hover:bg-gray-200"
    }`;

  return (
    <nav className="bg-white shadow-md p-4">
      <div className="flex justify-center space-x-4">
        <NavLink
          to="/payslip"
          className={linkClass("Payslip")}
          onClick={() => handleTabClick("Payslip")}
        >
          Payslip
        </NavLink>
        <NavLink
          to="/employee"
          className={linkClass("Employee")}
          onClick={() => handleTabClick("Employee")}
        >
          Employee
        </NavLink>
        <NavLink
          to="/department"
          className={linkClass("Department")}
          onClick={() => handleTabClick("Department")}
        >
          Department
        </NavLink>
      </div>
    </nav>
  );
}

export default Navbar;
